    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
      String nombre_articulo=txt_nombre_articulo.getText();
      int codigo_articulo=cmb_cod_articulos.getSelectedIndex();
      int cantidad_vendida=Integer.parseInt(txt_cant_vendida.getText());
      ejercicio_3_Venta_Articulos vent1 = new ejercicio_3_Venta_Articulos(codigo_articulo, nombre_articulo,cantidad_vendida);
      vent1.calcular();
      vent1.imprimir();
      
    }//GEN-LAST:event_jButton1ActionPerformed

