    public void setDatos(Aviones oa){
        jSpinField1.setValue(oa.getId());
        jComboBox2.setSelectedItem(oa.getNombre_avion());
        jComboBox1.setSelectedItem(oa.getDestino());
        jSpinField2.setValue(oa.getAdultos());
        jSpinField3.setValue(oa.getNiños());
        jSpinField4.setValue(oa.getTercera_edad());
    }
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        if (jSpinField1.getValue() >= 1 && !jComboBox1.getSelectedItem().equals("") && !jComboBox2.getSelectedItem().equals("")) {
            int id = jSpinField1.getValue();
            String nom = jComboBox2.getSelectedItem().toString();
            String desti = jComboBox1.getSelectedItem().toString();
            int adu = jSpinField2.getValue();
            int niñ = jSpinField3.getValue();
            int tercer = jSpinField4.getValue();

            int cant = adu + niñ + tercer;

            Aviones oa = new Aviones(id, nom, desti, tercer,adu,niñ);

            if (Funcion.equals("Insertar")) {
                Tlista_Aviones.Insertar(oa);
            }

            if (Funcion.equals("Modificar")) {

                Tlista_Aviones.Modificar(oa, Tlista_Aviones.Buscar(id));
            }

            frm_Admin_Aviones.VisualizarLista();
            this.dispose();
        } else {
            JOptionPane.showMessageDialog(null, "Ingrese correctamente los datos");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

