    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if (jComboBox1.getSelectedIndex() >= 0 && !jTextField1.getText().toString().equals("") && !jTextField2.getText().toString().equals("") && !jTextField3.getText().toString().equals("")) {
            int id=Integer.parseInt(jTextField1.getText());
            String Nombre=jTextField2.getText();
            String Sucursal=jComboBox1.getSelectedItem().toString();
            int NumSucursal=jComboBox1.getSelectedIndex()+1;
            String transaccion=jComboBox2.getSelectedItem().toString();
            double monto=Double.parseDouble(jTextField3.getText());
            Banco ob =  new Banco(id, Nombre, Sucursal, NumSucursal, transaccion, monto);
            Tlista.Insert(ob);
            FrmAdmin.VisualizarLista();
        }else{
            JOptionPane.showMessageDialog(null, "Ingrese correctamente los datos");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

