    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
     String cedula=txt_ced.getText();
     String nombre = txt_nombre.getText();
     String sexo="";
     if (jRadioButton1.isSelected()){
     sexo="Masculino";}
     if(jRadioButton2.isSelected()){
         sexo="Femenino";
     }
     String estado = cmb_estado.getSelectedItem().toString();
     String telefono = txt_telf.getText();
     String edad="50";
     personas Dalton = new personas(cedula, nombre, edad, sexo, estado, telefono);
        JOptionPane.showMessageDialog(null, Dalton.imprimirt());
    }//GEN-LAST:event_jButton1ActionPerformed

