    private void btn_busquedad_sActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_busquedad_sActionPerformed
    txt_mostrar.setText("");
        String dato = txt_busqueda.getText();
        int pos= busquedaSecuencial(dato);
        if(pos>=0)
        txt_mostrar.append("\n La Ciudad "+dato+" está registrada en la posición " + (pos+1) );
        else txt_mostrar.append("\n Ciudad no registrada");
        txt_busqueda.setText("");
    
    }//GEN-LAST:event_btn_busquedad_sActionPerformed

