    public static int busquedaSecuencial(String dato){
        for(int i=0; i<N; i++){
            if(dato.equalsIgnoreCase(V[i])){
                return i;
            }
        }
        return -1;
    }
    public void busquedaParcial(String cri){
        for(int i=0; i<N; i++){
            if (V[i].toLowerCase().contains(cri.toLowerCase()))
                txt_mostrar.append(V[i]+"\n ");
        }
    }
    public static void ingresar(){
        String cad= txt_ingreso.getText();
        String[] vectorcad =cad.split(",");
         N = vectorcad.length;
        for(int i = 0; i<N ; i++){
            V[i] = vectorcad[i];
            caja_1.addItem(V[i]);
        }
        //CB_ListaC.addItem(ciudades);
        txt_ingreso.setText("");
    }
    public void mostrar(){
        for(int i=0; i<N; i++){
            txt_mostrar.append(V[i]+"\n");
	}
    }
   public static void eliminar(){
        String dato = caja_1.getSelectedItem().toString();
        int E = caja_1.getSelectedIndex();
        caja_1.removeItemAt(E);
        int x = busquedaSecuencial(dato);
        for (int i = x; i < N; i++) {
            V[i]=V[i+1];
        }
        N--;
    }
   public static void actualizar(){
        String dato = caja_1.getSelectedItem().toString();
        int x = busquedaSecuencial(dato);
        if(x>=0){
            String actual = txt_modificar.getText();
            V[x]=actual;
        }
        caja_1.removeAllItems();
        for(int i = 0; i<N ; i++){
            caja_1.addItem(V[i]);
        }
    }
   public  void limpiar(){
   txt_mostrar.setText("");
   txt_ingreso.setText("");
   txt_busqueda.setText("");
   txt_modificar.setText("");
   }
