    public void ordenar(){
    for(int i=0; i<N-1; i++){
			for(int j=i+1;j<N;j++){
				if(V[j].compareToIgnoreCase(V[i])<0){
					String aux=V[i];
					V[i]=V[j];
					V[j]=aux;
                                        
                                        
				}
			}
		}
	caja_1.removeAllItems();
        for(int i = 0; i<N ; i++){
            caja_1.addItem(V[i]);
        }
    }

